import React ,{ useState, useEffect }from 'react'
import { GoogleLogin, GoogleOAuthProvider,googleLogout, useGoogleLogin } from "@react-oauth/google";
import axios from 'axios';


const GAuth = () => {
    const [ profile, setProfile ] = useState([]);
    const login = useGoogleLogin({
        onSuccess: (codeResponse) => setUser(codeResponse),
        onError: (error) => console.log('Login Failed:', error)
    });
    useEffect(
        () => {
            if (user) {
                axios
                    .get(`https://www.googleapis.com/oauth2/v1/userinfo?access_token=${user.access_token}`, {
                        headers: {
                            Authorization: `Bearer ${user.access_token}`,
                            Accept: 'application/json'
                        }
                    })
                    .then((res) => {
                        setProfile(res.data);
                    })
                    .catch((err) => console.log(err));
            }
        },
        [ user ]
    );
    const clientId = "691701543397-gbptsop3a1t7b0j2b25djg67nae1rkik.apps.googleusercontent.com";

    return (
        <GoogleOAuthProvider 
        style={{backgroundColor: '#f2f5f9'}}
            clientId={clientId}>
            <GoogleLogin
                onSuccess={credentialResponse => {
                    console.log(credentialResponse);
                }}
                onError={() => {
                    console.log("Login Failed");
                }}
            />
        </GoogleOAuthProvider>
    );
};
export default GAuth;
